# create .docx file based on PDF layout data with
# python package python-docx
# 

import os
from io import BytesIO
from docx import Document
from docx.shared import Pt
from docx.enum.section import WD_SECTION


def make_page(doc, layout):
    '''create page based on layout data.
       to avoid incorrect page break from original document,
       a new page section is created for each page
    '''

    # new page section
    # a default section is created when initialize the document,
    # so we do not have to add section for the first time.
    if not doc.paragraphs:
        section = doc.sections[0]
    else:
        section = doc.add_section(WD_SECTION.NEW_PAGE)
    width, height = layout['width'], layout['height']
    section.page_width  = Pt(width)
    section.page_height = Pt(height)

    # set page margin
    left,right,top,bottom = layout['margin']    
    section.left_margin = Pt(left)
    section.right_margin = Pt(right)
    section.top_margin = Pt(top)
    section.bottom_margin = Pt(bottom)
    max_width = width - right

    # reference blocks:
    # - ref_table indicates whether previous block is in table format
    # - ref_block is used to calculate space between two paragraphs. Considering
    #   that vertical paragraph and images are created in float format, they should
    #   be excluded.
    ref_table, ref_block = None, None

    for block in layout['blocks']:
        # make paragraphs
        if block['type']==0:
            if block['lines'][0]['dir'] == (1.0 ,0):
                make_paragraph(doc, block, ref_block, left)
                ref_block = block
            else:
                make_vertical_paragraph(doc, block)

            ref_table = None

        # make image
        elif block['type']==1:
            # make_image(doc, block)
            ref_table = None

        # make table
        else:
            ref_table = make_table(doc, ref_table, block, max_width)
            ref_block = block

   

def make_paragraph(doc, block, ref_block, left_margin):
    '''create paragraph for a text block.
       join line sets with TAB and set position according to bbox
    '''
    lines = block['lines']
    s = 'Normal' if lines[0]['wmode']==0 else 'List Bullet'
    p = doc.add_paragraph(style=s)

    # paragraph format
    pf = p.paragraph_format
    pf.line_spacing = 1 # single
    pf.space_before = Pt(0)
    pf.space_after = Pt(0)
    pf.left_indent = Pt(0)
    pf.right_indent = Pt(0)

    if ref_block:
        space_after = max(block['bbox'][1]-ref_block['bbox'][3], 0)
        doc.paragraphs[-2].paragraph_format.space_after = Pt(space_after*0.8)

    # text formats
    for i, line in enumerate(lines):
        pos = line['bbox'][0]-left_margin        
        if i==0:
            pf.left_indent = Pt(pos)
        else:
            pf.tab_stops.add_tab_stop(Pt(pos))
            p.add_run('\t')

        # flags is an integer, encoding bools of font properties:
        # bit 0: superscripted (2^0)
        # bit 1: italic (2^1)
        # bit 2: serifed (2^2)
        # bit 3: monospaced (2^3)
        # bit 4: bold (2^4)
        flags = line['spans']['flags']
        span = p.add_run(line['spans']['text'])
        span.italic = bool(flags & 2**1)
        span.bold = bool(flags & 2**4)

        # font
        span.font.name = line['spans']['font']
        span.font.size = Pt(line['spans']['size'])
    

def make_vertical_paragraph(doc, block):
    pass

def make_image(doc, block):
    '''create image with bytes data stored in block.
       we could create a temporary image file first and delete it after inserting to word,
       but a file-like stream is supported and it's more convenient
    '''
    doc.add_picture(BytesIO(block['image']), width=Pt(block['width']))

def make_table(doc, table, block, max_width):
    '''create table for a text block
       count of columns are checked, combine rows if next block is also in table format
    '''
    lines = block['lines']

    # check count of columns
    boundaries = [int(line['bbox'][0]) for line in lines]
    boundaries = list(set(boundaries))
    boundaries.sort()

    # check table
    if not table or len(table.columns)!=len(boundaries):
        table = doc.add_table(rows=1, cols=len(boundaries))
        cells = table.rows[0].cells

        # set column width
        right_boundaries = boundaries[1:] + [max_width]
        for i, (l,r) in enumerate(zip(boundaries, right_boundaries)):
            cells[i].width = Pt(r-l)
    else:
        cells = table.add_row().cells
    
    # insert into cells
    for i, x in enumerate(boundaries):
        cell_lines = list(filter(lambda line: int(line['bbox'][0])==x, lines))
        cells[i].text = '\n'.join([line['spans']['text'] for line in cell_lines])

    return table


